import React from 'react';
import { Col, Container,Image, Row, Card, Button } from 'react-bootstrap';

const ProductsPage = () => {

    const product = {id:"1",
                    name:"Longines Master Collection",
                    price:90.00,
                    img:"https://luks33.ru/image/cache/catalog/mens/0/timenn-ru-image-cache-data-Longines-rovno-longines-20master-20goldentimenn-ru-3-990x990-900x900.jpg",
                    quantity:10,
                    categoryType:{id:"2",
                                name:"Quartz Watches",
                                description:"Built with quartz battery at the very core, quartz watch movements are one of the most reliable forms of timekeeping"}}
        
    return (
        <Container style={{marginTop:20, justifyContent:'center', alignItems:'center', display:'flex'}}>
            <Col style={{ justifyContent:'left'}}>
                <Image width={500} height={500} src={product.img}/>
            </Col>
            <Col md={4}>
                <Row>
                    <h2>{product.name}</h2>
                    <div>
                        {product.categoryType.name}
                    </div>
                </Row>
            </Col>
            <Col md={4}>
                <Card>
                    <h3>{product.price}</h3>
                    <Button variant="outlined" color="black" >Add to basket</Button>
                </Card>
            </Col>
        </Container>
    );
};

export default ProductsPage;
